mapper.select:

select no, name, email, gender
  from user
 where no = #{no }


select no, name, email, gender
  from user
 where email = #{email}

choose.when:

update user
   set name=#{name },
       gender=#{gender }
 where no=#{no }

choose.otherwise:

update user
   set name=#{name },
       password=password(#{password }),
       gender=#{gender }
 where no=#{no }
